{
    name: 'all',
    unary: true,
    logic: _ => true,
  }

{
    name: 'true',
    unary: true,
    logic: target => target === true,
  }

{
    name: 'false',
    unary: true,
    logic: target => target === false,
  }

{
    name: 'empty',
    unary: true,
    logic: target => !isDefined(target),
  }

{
    name: 'nonEmpty',
    unary: true,
    logic: target => isDefined(target),
  }

{
    name: 'equals',
    unary: false,
    logic: (target, searchTerm) => target === searchTerm,
  }

{
    name: 'doesNotEqual',
    unary: false,
    logic: (target, searchTerm) => target !== searchTerm,
  }

{
    name: 'greaterThan',
    unary: false,
    logic: (target, searchTerm) => target > searchTerm,
  }

{
    name: 'lessThan',
    unary: false,
    logic: (target, searchTerm) => target < searchTerm,
  }

{
    name: 'greaterThanOrEqual',
    unary: false,
    logic: (target, searchTerm) => target >= searchTerm,
  }

{
    name: 'lessThanOrEqual',
    unary: false,
    logic: (target, searchTerm) => target <= searchTerm,
  }

{
    name: 'empty',
    unary: true,
    logic: target => !isDefined(target) || isNaN(target),
  }

{
    name: 'notEmpty',
    unary: true,
    logic: target => isDefined(target) && !isNaN(target),
  }

{
    name: 'contains',
    unary: false,
    logic: (target, searchTerm, caseSensitive?) =>
      normalizeCase(target, caseSensitive).includes(normalizeCase(searchTerm, caseSensitive)),
  }

{
    name: 'doesNotContain',
    unary: false,
    logic: (target, searchTerm, caseSensitive?) =>
      !normalizeCase(target, caseSensitive).includes(normalizeCase(searchTerm, caseSensitive)),
  }

{
    name: 'startsWith',
    unary: false,
    logic: (target, searchTerm, caseSensitive?) =>
      normalizeCase(target, caseSensitive).startsWith(normalizeCase(searchTerm, caseSensitive)),
  }

{
    name: 'endsWith',
    unary: false,
    logic: (target, searchTerm, caseSensitive?) =>
      normalizeCase(target, caseSensitive).endsWith(normalizeCase(searchTerm, caseSensitive)),
  }

{
    name: 'equals',
    unary: false,
    logic: (target, searchTerm, caseSensitive) =>
      normalizeCase(target, caseSensitive) === normalizeCase(searchTerm, caseSensitive),
  }

{
    name: 'doesNotEqual',
    unary: false,
    logic: (target, searchTerm, caseSensitive) =>
      normalizeCase(target, caseSensitive) !== normalizeCase(searchTerm, caseSensitive),
  }

{
    name: 'empty',
    unary: true,
    logic: target => !isDefined(target) || target.length < 1,
  }

{
    name: 'notEmpty',
    unary: true,
    logic: target => isDefined(target) && target.length > 0,
  }
